var BadgeForceToken = artifacts.require("./BadgeForceToken.sol");

contract('BadgeForceToken', (accounts) => {
  let badgeForceToken;
  beforeEach(async  () => {
    badgeForceToken = await BadgeForceToken.new();
  });
  it("should burn BFT for issuing and update total creds issued", async () => {
    const balance = await badgeForceToken.balanceOf(accounts[0]);
    await badgeForceToken.burnForIssue();
    const newBalance = await badgeForceToken.balanceOf(accounts[0]);
    assert.equal(balance - .00001, newBalance);
  });  
});
