pragma solidity ^0.4.15;

import "badgeforce/contracts/IssuerInterface.sol";
import "badgeforce/contracts/HolderInterface.sol";

contract Verifier {

    string constant INVALID_TRANSACTION = "Invalid transaction: the transaction does not exist";
    string constant INVALID_INTEGRITYHASH = "Invalid data integrity: data in credential does not match original transaction data";
    string constant INVALID_RECIPIENT = "Invalid recipient: recipient does not match original transaction data";
    string constant INVALID_TXTKEY = "Invalid transaction key: transaction keys don't match";
    string constant REVOKED = "Invalid credential: credential revoked";
    string constant VALID_CREDENTIAL = "Credential is valid";
    mapping (uint=>string) errCodeMap;
    address constant NONE = 0x0000000000000000000000000000000000000000;

    function Verifier() public {
        errCodeMap[0] = INVALID_TRANSACTION;
        errCodeMap[1] = INVALID_INTEGRITYHASH;
        errCodeMap[2] = INVALID_RECIPIENT;
        errCodeMap[3] = INVALID_TXTKEY;
        errCodeMap[4] = REVOKED;
        errCodeMap[5] = VALID_CREDENTIAL;
    }

    function verifyCredential(bytes32 _txtKey, bytes32 _integrityHash, address _recipient, address _issuer) constant public returns(bool isVerified, uint errCode) {
        var (_revoked, _integrityHashCheck, _recipientCheck) = checkTransaction(_txtKey, _integrityHash, _recipient, _issuer);
        if (_revoked) {
            return(false, 4);
        } else if (!_recipientCheck) {
            return(_recipientCheck, 2);
        } else if (!_integrityHashCheck) {
            return(_integrityHashCheck, 1);
        } else {
            return(true, 5);
        }
    }

    /// @notice check that a transaction key exists in the transaction map (verify credential issuer)
    /// @param _txtKey the transaction key to check 
    function checkTransaction(bytes32 _txtKey, bytes32 _integrityHash, address _recipient, address _issuer) constant public returns(bool _revoked,  bool _integrityHashCheck, bool _recipientCheck) {
        IssuerInterface issuer = IssuerInterface(_issuer);
        var (key, integrityHash, recipient) = issuer.getTransaction(_txtKey);
        _revoked = false;
        _integrityHashCheck = false;
        _recipientCheck = false;
        if (recipient == NONE) {
            return(_revoked, _integrityHashCheck, _recipientCheck);
        } 

        _revoked = issuer.isRevoked(key);
        _integrityHashCheck = (_integrityHash == integrityHash);
        _recipientCheck = (_recipient == recipient);
        return(_revoked, _integrityHashCheck, _recipientCheck);
    }
}
