var Web3 = require('web3');
var express = require('express');
var app = express();
var provider = new Web3.providers.HttpProvider('http://127.0.0.1:8000');
var web3 = new Web3(provider);
app.get('/check', function (req, res) {
    if (!web3.net.listening) {
        res.status(400).send('ganache server not ready');
    }
    else {
        res.status(200).send('ganache server ready');
    }
});
app.listen(3000, function () { return console.log('Ganache test helper server listening on port 3000'); });
