export class ContractConfig {
	setAddress: any;
    constructor(public address: string){
    	this.setAddress = jest.fn();
    }
}

export class Contract {

	public contractABI: any;

	constructor(public config: ContractConfig) {
		this.contractABI = {
			at: jest.fn(() => {
				return {};
			})
		}
	}

   	getInstance() {
    	return this.contractABI.at(this.config.address);
	}

    setAddress(address) {
        this.config.setAddress(address);
    }
}
