jest.mock("../contract-api.ts");

import {Contract, ContractConfig} from "../contract-api";

describe("contract api", () => {
	let contract;
	let config;
	beforeEach(() => {
		config = new ContractConfig("0x0", "0", 0, 0);
		contract = new Contract(config);
	});
	afterAll(() => {
		jest.clearAllMocks();
	});
	it("should return instance of contract", () => {
		const instance = contract.getInstance();
		expect(contract.contractABI.at).toHaveBeenCalled();
		expect(instance).toBeDefined();
	});
})
