export class HolderInfo {
    /**
     * Holder info object helper
     * @param address address of the holder
     */
    constructor(public address: string) {}
}

export class Credential {
    /**
     * Credential object helper
     * @param issuer      address of the issuer who issued this credential
     * @param description
     * @param name
     * @param image
     * @param version
     * @param expires     expiration date
     * @param recipient   address of the holder contract
     * @param txKey       transaction key of the credential
     * @param active      flag for pending credentials
     */
    constructor(
        public issuer?: string, public description?: string, public name?: string, public image?: string,
        public version?: string, public expires?: string, public recipient?: string, public txKey?: string, public active?: boolean
    ) {}
}
