const HolderFactory = artifacts.require(`./HolderFactory.sol`)
const BadgeLibrary = artifacts.require("BadgeLibrary.sol");

module.exports = (deployer) => {  
    deployer.deploy([BadgeLibrary]);
    deployer.link(BadgeLibrary, [HolderFactory]); 
    deployer.deploy(HolderFactory, {gas: 5712389});
}